sysauto_script.script:
//Codigo para obtener la fecha acual
var rigthNow = new GlideDateTime();
//Consultar a la base de datos de NeedIt Tasks cuales archivos tienen fecha de vencimiento pronta 
//24 horas desde ahora. solamente retornar los archivos que no tengan el "state" en closed complete
var dueSoon = new GlideRecord ('x_58872_needit_needit_task');
dueSoon.addQuery('due_date','>=',rightNow);
dueSoon.addQuery('due_date','<',gs.hoursAgo(-24));
dueSoon.addQuery('state','!3',3);
//para ejecutar el codigo se debe poner ".query" despues de la variable declarada
dueSoon.query();

//Write un log message for each overdue NeedIt Task Record
while(dueSoon.next()){
	gs.info("NeedIt Task due soon record =" + dueSoon.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',dueSoon,dueSoon.number,gs.getUser());
}
